export const authReducer = ({state,action}) =>{
    const {type,payload: {isAuthticated,user,authLoading}} = action
    switch (type) {
        case 'SET_AUTH':
            return {
                ...state,
                authLoading:false,
                isAuthticated
            }
        default:
            break;
    }
}
