import { POSTS_LOADED_FAIL, POSTS_LOADED_SUCCESS } from "../contexts/constants";

export const PostsReducer = (state, action) => {
  const { type, payload } = action;
  switch (type) {
    case POSTS_LOADED_SUCCESS:
      console.log(payload)
      return {
        ...state,
        posts: payload,
        postsLoading: false,
      };
    case POSTS_LOADED_FAIL:
      return {
        ...state,
        posts: [],
        postsLoading: false,
      };
    default:
      return state;
  }
};
