export const authReducer = ({state,action}) =>{
    const {type,payload: {isAuthticated,user,authLoading}} = action
    switch (action.type) {
        case 'SET_AUTH':
            console.log(action)
            return {
                ...state,
                authLoading : action.payload.authLoading,
                isAuthticated: action.payload.isAuthticated,
                user : action.payload.user
            }
        default:
            return state
    }
}
